import MultiSelect from  'react-multiple-select-dropdown-lite'
import  'react-multiple-select-dropdown-lite/dist/index.css';
import { Button, Text,Heading, Box, Input,FormControl,FormLabel,InputLeftAddon,InputGroup } from "@chakra-ui/react"
import Recaptcha from "react-recaptcha";

function Home() {
    const data = [
        {
          value: 1,
          label: "Dowry"
        },
        {
          value: 2,
          label: "Harassment"
        },
        {
          value: 3,
          label: "Rape"
        },
        {
          value: 4,
          label: "PHP"
        },
        {
          value: 5,
          label: "C#"
        },
        {
          value: 6,
          label: "Go"
        }
      ];
      const [selectedOption, setSelectedOption] = useState(null);
 
      // handle onChange event of the dropdown
      const handleChange = e => {
        setSelectedOption(e);
      }
    return (
        <div className="home">
            <Box class="Box" maxW="80%" p="3.125rem 0rem 3.125rem 6.25rem">
            <Heading style={{textAlign :"center",marginBottom:".30rem"}} fontSize="xxl" >Submit A Helpline No.</Heading>
            
            
            <div style={{marginTop:".30rem", marginLeft:".50rem"}} class="Sput">
           
        
            
            <FormControl style={{paddingBottom:".20rem"}} id="first-name" isRequired>
            <FormLabel>Organization Name</FormLabel>
            <Input  placeholder="Organization Name" />
            </FormControl>
            <FormControl style={{paddingBottom:".20rem"}} id="first-name" isRequired>
            <FormLabel>Address of the organization</FormLabel>
            <Input  placeholder="Organization Name" />
            </FormControl>
            <FormControl style={{paddingBottom:".20rem"}} id="first-name" isRequired>
            <FormLabel>Your Phone Number</FormLabel>
            <InputGroup>
            <InputLeftAddon fontSize="0.5rem" children="+91/+022" />
            <Input type="tel" placeholder="phone number" />
            </InputGroup>
            </FormControl>
            <FormControl style={{paddingBottom:".20rem"}} id="first-name" isRequired>
            <FormLabel>Location</FormLabel>
            <Input placeholder="Location" />
            </FormControl>
            
          
                <FormLabel style={{fontSize:".30rem"}} size="xl">Issues</FormLabel>

                {/* {value}
           

            <MultiSelect className= "ph" hidePlaceholder={true} placeholder = "search" 
                
                options={options} /> */}
        </div>
            <Button style={{marginLeft:"50px"}}size="lg" colorScheme="teal" mt=".34rem">
                    Submit
            </Button>
            <Recaptcha
              sitekey="6LfCqn8cAAAAAMtY5AFClOkoiqVal5pSBtuZT9ar"
              render="explicit"
            //   onloadCallback={this.recaptchaLoaded}
            //   verifyCallback={this.verifyCallback}
            //   onClick={this.handleSubscribe}
            />
            </Box>
        </div>
        
    )
}

export default Home;
